e="echo -e"; isc=$(iris qlist); hr="======================="; ${e} "\nThis a list of ports necessary for system operation\n"; ${e} "\nAssumed Ports\n${hr}\nssh: 22\nsftp: 2022\nISC Agent: 2188";${e} "${isc}"|awk -F^ -v hr=$hr 'BEGIN {print "\nSuper Server Ports\n"hr} {print $1": "$6}'; ${e} "\nLicense Ports (TCP & UDP)\n$hr";for i in $(echo "${isc}"|awk -F^ '{print $1"^"$2"/"$5}'); do awk -F, -v instance=${i%%^*} '/LicenseServer/{getline;print instance": "$2}' ${i##*^}|head -1;done;${e} "\nHyperspace Ports\n$hr";for line in $(/epic/bin/epiccontrol printenvs|tail -n +3|awk '{print $1"^"$2}'); do env=${line#*^}; inst=${line%^*};${e} "$env: \c";(${e} "w \"~~\"_^%ZeOSUNQ(\"EPICCOMM\",\"TCP\",\"PORT\"),! h" | (irissession $inst -U $env)|awk -F~ '/^~~/{print $3} /^Insta/{print $0}') ;done; ${e} "\nESMP Port\n${hr}"; awk -F'[<>]' '/HTTPD_PORT/{print $3}' /epic/redalert/conf/esmp.conf
